CREATE TABLE Publishers (
	PublisherId SERIAL PRIMARY KEY,
	CompanyName VARCHAR(200) NOT NULL UNIQUE
);

CREATE TABLE Authors (
	AuthorId SERIAL PRIMARY KEY,
	FirstName VARCHAR(100) NOT NULL,
	LastName VARCHAR(100) NOT NULL
);

CREATE TABLE Books (
	BookId SERIAL PRIMARY KEY,
	AuthorId INTEGER NOT NULL REFERENCES Authors (AuthorId) ON DELETE CASCADE,
	PublisherId INTEGER NOT NULL REFERENCES Publishers (PublisherId) ON DELETE CASCADE,
	Title VARCHAR(4000) NOT NULL,
	ISBN BIGINT NULL,
	Summary TEXT NULL
);

CREATE TABLE Users (
	UserId SERIAL PRIMARY KEY,
	LogonName VARCHAR(400) NOT NULL UNIQUE,
	FirstName VARCHAR(100) NOT NULL,
	LastName VARCHAR(100) NOT NULL,
	IsAdmin BOOLEAN NOT NULL DEFAULT FALSE,
	PasswordHash VARCHAR(255) NOT NULL
);

CREATE TABLE Keywords (
	KeywordId SERIAL PRIMARY KEY,
	BookId INTEGER NOT NULL REFERENCES Books (BookId) ON DELETE CASCADE,
	Keyword VARCHAR(100) NOT NULL,
	UNIQUE(BookId, Keyword)
);

























